import { Effect } from "./typings";
import { sleep } from "../utils/time";
import { random } from "../utils/number";

const sources = [
  `data:image/png;base64,`,
  `data:image/png;base64,`,
  `data:image/png;base64,`,
  `data:image/png;base64,`,
  `data:image/png;base64,`,
];
const brushes = sources.map((src) => {
  const brush = new Image();
  brush.src = src;
  return brush;
});

const selectBrush = () => brushes[random(sources.length - 1)];

export const unveil: Effect<{ wait?: number }> =
  (srcCanvas) =>
  async ({ touch: { x, y }, params: { radius = 25, hardness = 1 }, wait = 50 }) => {
    if (!srcCanvas || !srcCanvas.context) return;
    const brush = selectBrush();
    if (!brush) return;
    console.log("unveil", brush);
    const width = Math.round(radius as number);
    const height = Math.round(radius as number);
    brush.width = width;
    brush.height = height;
    const drawX = x - width > 0 ? x - width : 0;
    const drawY = y - height > 0 ? y - height : 0;
    const draw = async (alpha = 0): Promise<void> => {
      if (alpha > 127) return;
      srcCanvas.context.save();
      srcCanvas.context.globalCompositeOperation = "destination-out";
      srcCanvas.context.filter = "blur(3px)";
      srcCanvas.context.globalAlpha = Math.floor(alpha);
      srcCanvas.draw(brush, drawX, drawY);
      srcCanvas.context.filter = "none";
      srcCanvas.context.restore();
      if (alpha < 255) return sleep(wait).then(() => draw(alpha + (1 + (hardness as number)) * 2));
    };
    return draw();
  };
